import React, { useState } from "react";

const artistsOfWeek = [
  { id: 1, name: "Aria", img: "https://randomuser.me/api/portraits/women/44.jpg" },
  { id: 2, name: "Sami", img: "https://randomuser.me/api/portraits/men/46.jpg" },
  { id: 3, name: "Rima", img: "https://randomuser.me/api/portraits/women/47.jpg" },
];

const exhibitions = [
  { id: 1, title: "Alox Sunrise", img: "https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=facearea&w=300&q=80" },
  { id: 2, title: "Kanac Rain", img: "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=facearea&w=300&q=80" },
  { id: 3, title: "Vivid Delish", img: "https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=facearea&w=300&q=80" },
];

const sampleProfile = {
  name: "Aria",
  genre: "Impressionism",
  education: "Lorem Ipsum",
  artworks: [
    "https://images.unsplash.com/photo-1464983953574-0892a716854b?auto=format&fit=facearea&w=300&q=80",
    "https://images.unsplash.com/photo-1506744038136-46273834b3fb?auto=format&fit=facearea&w=300&q=80",
    "https://images.unsplash.com/photo-1519125323398-675f0ddb6308?auto=format&fit=facearea&w=300&q=80",
  ],
  bio: "Lorem ipsum dolor sit amet, qui dicta dolor sit qui impedit vero sit omnis minima. At quia sint est sequi quidem aut maiores eveniet sit repellendus incidunt. Et esse quasi et omnis.",
};

const exhibitionDetails = {
  title: "EARNIC RAIN BY RIMA CHAMPION",
  img: "https://upload.wikimedia.org/wikipedia/commons/3/3d/Lawrence_Alma-Tadema_-_A_Coign_of_Vantage.jpg",
  details: "Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.",
  artist: { name: "Rima", img: "https://randomuser.me/api/portraits/women/47.jpg" },
};

export default function App() {
  const [selectedShow, setSelectedShow] = useState(null);
  const [nav, setNav] = useState("home");
  const [form, setForm] = useState({ name: "", email: "", time: "" });
  const [submitted, setSubmitted] = useState(false);

  const handleBook = (show) => {
    setSelectedShow(show);
    setNav("book");
    setSubmitted(false);
    setForm({ name: "", email: "", time: "" });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <header className="bg-white shadow p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-gray-800">Virtual Art Gallery</h1>
        <nav>
          <button className={`mx-2 text-blue-600 hover:underline ${nav === "home" ? "font-bold" : ""}`} onClick={() => setNav("home")}>Home</button>
          <button className={`mx-2 text-blue-600 hover:underline ${nav === "book" ? "font-bold" : ""}`} onClick={() => setNav("book")}>Book Appointment</button>
        </nav>
      </header>
      <main className="p-8 max-w-2xl mx-auto">
        {nav === "home" && (
          <>
            <h2 className="text-xl font-semibold mb-4">Upcoming Art Shows</h2>
            <ul className="space-y-6">
              {sampleShows.map((show) => (
                <li key={show.id} className="bg-white rounded shadow p-4">
                  <h3 className="text-lg font-bold">{show.title}</h3>
                  <p className="text-gray-700">By {show.artist} &middot; {show.date}</p>
                  <p className="mt-2">{show.description}</p>
                  <button
                    className="mt-4 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700"
                    onClick={() => handleBook(show)}
                  >
                    Book Appointment
                  </button>
                </li>
              ))}
            </ul>
          </>
        )}
        {nav === "book" && (
          <div className="bg-white rounded shadow p-6">
            <h2 className="text-xl font-semibold mb-4">Book Appointment</h2>
            {selectedShow && (
              <div className="mb-4">
                <h3 className="font-bold">{selectedShow.title}</h3>
                <p>By {selectedShow.artist} &middot; {selectedShow.date}</p>
              </div>
            )}
            {submitted ? (
              <div className="text-green-600 font-semibold">Appointment booked! Check your email for confirmation.</div>
            ) : (
              <form onSubmit={handleSubmit} className="space-y-4">
                <input
                  type="text"
                  required
                  placeholder="Your Name"
                  className="w-full p-2 border rounded"
                  value={form.name}
                  onChange={e => setForm({ ...form, name: e.target.value })}
                />
                <input
                  type="email"
                  required
                  placeholder="Your Email"
                  className="w-full p-2 border rounded"
                  value={form.email}
                  onChange={e => setForm({ ...form, email: e.target.value })}
                />
                <input
                  type="datetime-local"
                  required
                  placeholder="Preferred Time"
                  className="w-full p-2 border rounded"
                  value={form.time}
                  onChange={e => setForm({ ...form, time: e.target.value })}
                />
                <button type="submit" className="w-full py-2 bg-blue-600 text-white rounded hover:bg-blue-700">Book</button>
              </form>
            )}
            <button className="mt-4 text-blue-600 hover:underline" onClick={() => setNav("home")}>Back to Shows</button>
          </div>
        )}
      </main>
    </div>
  );
}
